DROP TABLE IF EXISTS `user`;
CREATE TABLE `user` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '用户编号',
  `name` varchar(255) DEFAULT NULL COMMENT '姓名',
  `phone` varchar(255) DEFAULT NULL COMMENT '手机号码',
  `password` varchar(255) DEFAULT NULL COMMENT '密码',
  `sex` tinyint(255) DEFAULT NULL COMMENT '性别（0代表女，1代表男）',
  `idcard` varchar(255) DEFAULT NULL COMMENT '昵称',
  `borndate` date DEFAULT NULL COMMENT '出生日期',
  `address` varchar(255) DEFAULT NULL COMMENT '地址',
  `degree` varchar(255) DEFAULT NULL COMMENT '学历',
  `introduction` text COMMENT '自我介绍',
  `qualification` varchar(255) DEFAULT NULL COMMENT '专业资质',
  `qualificationid` varchar(255) DEFAULT NULL COMMENT '资质编号',
  `field` tinyint(4) DEFAULT NULL COMMENT '擅长领域（1-婚恋、2-健康、3-两性、4-职场、5-教育、6-性格、7-其他）',
  `college` varchar(255) DEFAULT NULL COMMENT '毕业院校',
  `role` tinyint(11) DEFAULT NULL COMMENT '角色（0代表超级管理员，1代表管理员，2代表咨询师，3代表咨询者',
